import * as React from "react";
import {useState} from "react";
import Box from "@mui/material/Box";
import {
  Grid,
  Typography,
  Stack,
  TextField,
  Button,
  InputAdornment,
} from "@mui/material";
import PersonIcon from "@mui/icons-material/Person";
import PhoneInTalkIcon from "@mui/icons-material/PhoneInTalk";
import EmailIcon from "@mui/icons-material/Email";
function Contact() {
      const [firstName, setFirstName] = useState("");
      const [lastName, setLastName] = useState("");
      const [age, setAge] = useState("");

      interface FormDataType {
        firstName: string;
        lastName: string;
        age: string;
      }
      const responseBody: FormDataType = {
        firstName: "",
        lastName: "",
        age: "0",
      };

      const onSubmitHandler = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();
        responseBody.firstName = firstName;
        responseBody.lastName = lastName;
        responseBody.age = age;
        console.log(JSON.stringify(responseBody));
        //Form submission happens here
      };
      const inputChangeHandler = (
        setFunction: React.Dispatch<React.SetStateAction<string>>,
        event: React.ChangeEvent<HTMLInputElement>
      ) => {
        setFunction(event.target.value);
      };
  return (
    <>
      <Box
        component="main"
        textAlign="center"
        sx={{ p: 3, backgroundColor: "#ddd" }}
      >
        <Grid container maxWidth="lg" spacing={5}>
          <Grid item xs={12} md={8}>
            <Box style={{ height: "200", width: "100%" }}>
              <Typography
                variant="h5"
                sx={{
                  borderBottom: "2px solid #000",
                  display: "inline-block",
                  "&:hover, &.Mui-focusVisible": {
                    borderBottom: "2px solid #E10700",
                  },
                }}
              >
                CONTACT{" "}
                <span
                  style={{
                    color: "#E10700",
                  }}
                >
                  US
                </span>
              </Typography>
              <Typography
                variant="body2"
                sx={{
                  mt: 2,
                }}
              >
                Kachhwa Mirzapur, Uttar Pradesh 231001
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={12} md={4}>
            <Box>
              <form onSubmit={onSubmitHandler}>
                <Stack direction="column" spacing={2}>
                  <TextField
                    id="outlined-basic"
                    label="Full Name"
                    variant="outlined"
                    onChange={(e) => inputChangeHandler(setFirstName, e)}
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <PersonIcon />
                        </InputAdornment>
                      ),
                    }}
                  />
                  <TextField
                    id="outlined-basic"
                    label="Mobile Number"
                    variant="outlined"
                    onChange={(e) => inputChangeHandler(setFirstName, e)}
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <PhoneInTalkIcon />
                        </InputAdornment>
                      ),
                    }}
                  />
                  <TextField
                    fullWidth
                    id="outlined-basic"
                    label="Email Id"
                    variant="outlined"
                    InputProps={{
                      startAdornment: (
                        <InputAdornment position="start">
                          <EmailIcon />
                        </InputAdornment>
                      ),
                    }}
                  />
                  <Button variant="outlined">Submit</Button>
                </Stack>
              </form>
            </Box>
          </Grid>
        </Grid>
      </Box>
    </>
  );
}
export default Contact;
